  createCanvas(img) {
    const depthSrc = img.dataset.depthMap;
    const app = new PIXI.Application({ /* ... */ });
    // Apply DisplacementFilter
    // Subscribe to MotionInput
    // Add IntersectionObserver for visibility
  }
